import React from "react";
import "./App.css";
import "bootstrap/dist/css/bootstrap.min.css";
import { Navbar, Nav, NavDropdown } from "react-bootstrap";
import Interval from "./component/lifecycle";

const styleImg = { width: 400, height:430 };

class App extends React.Component {
  state = {
    shows: false,
    fullName: "mezrigui faycel",
    bio:
      "student with cmg",
    imgSrc: (
      <img src="/Toof.jpg" alt="" style={styleImg} />
    ),
    profession: "FullStack Developer",
  };


  render() {
    const buttonMsg = this.state.shows ? "Hide Profile" : "Show Profile";

    return (
      <div className="App">

        <div className="title">
          <h1>Profile:</h1>
        </div>
        {this.state.shows ? (
          <div className="profileElements">
            <h1>{this.state.fullName}</h1>
            <p>
              <b>Bio:</b>
              <br />
              {this.state.bio}
            </p>
            <div>{this.state.imgSrc}</div>
            <h3>{this.state.profession}</h3>
          </div>
        ) 
        : null}
        <button onClick={() => this.setState({ shows: !this.state.shows })}>
          {buttonMsg}
        </button>
        <Interval />
      </div>
    );
  }
}
export default App;
